settings.custom_liquid:
{% assign lowest_inventory = product.first_available_variant.inventory_quantity %}

{% for variant in product.variants %}
  {% if variant.available %}
    {% if variant.inventory_quantity < lowest_inventory %}
      {% assign lowest_inventory = variant.inventory_quantity %}
    {% endif %}
  {% endif %}
{% endfor %}


{% if product.tags contains "BFOOS" or product.tags contains "bfoos"%}
<span class="badge" aria-hidden="true" style="margin:0px">&#128308;
&nbsp;&nbsp;Out of stock</span>
{% else %}
<span class="badge" aria-hidden="true" style="margin:0px">&#128994;&nbsp;&nbsp;In stock - 


{{ lowest_inventory | divided_by: 3 }} Bundles Left

</span>
{% endif %}
