settings.custom_liquid:
{% if product.tags contains "BFOOS" %}
<button type="submit" name="add" class="product-form__submit button button--full-width button--secondary" disabled="">Sold out</button>
{% else %}
<style>
input:checked + label {
  color: white;background-color:black;border-color:black;
}
.radio-button {
  display: none;
}
.productFamily{     border: 1px solid; border-radius:50px; background-color:white;color:black; display: inline-block; margin-top: 5px
; margin-bottom: 0px
; padding: 10px 20px 10px 20px;font-size: 1.4rem; letter-spacing: .1rem; line-height: 1;text-align: center;margin-right: 6px;
; }
</style>

<script>
  function OpenWindow(){
    for(i=0;i<document.FormName["RB1"].length;i++){
      if(document.FormName["RB1"][i].checked){
    window.open(document.FormName["RB1"][i].value,"_self");
    break;
  }
}
  }
</script>

<p style="margin-bottom:0px">Select fitting type:</p>



<form name="FormName" style="margin-top:5px">
    <input class="radio-button" id="rad1" type="radio" name="RB1" value="https://www.lifx.com.au/cart/update?updates[40260732911663]=1&updates[40260761550895]=1&updates[40260732878895]=1" checked><label for="rad1" class="productFamily">E27 Screw</label>

<input class="radio-button" id="rad2" type="radio" name="RB1" value="https://www.lifx.com.au/cart/update?updates[40260758077487]=1&updates[40260758405167]=1&updates[40261162631215]=1"><label for="rad2" class="productFamily">B22 Bayonet</label>

<input class="button" type="button" value="Add to Cart" name="butt" onclick="OpenWindow()" style="width:100%;margin-top:25px">
  </form>

{% endif %}

{% if product.available%}
<span class="badge" aria-hidden="true" style="margin:0px">&#128994;&nbsp;&nbsp;In stock</span>
{% else %}
<span class="badge" aria-hidden="true" style="margin:0px">&#128308;
&nbsp;&nbsp;Out of stock</span>
{% endif %}
