settings.custom_liquid:
{% if product.tags contains 'B2S50' %}
<span class="badge color-accent-2" aria-hidden="true" style="float:right;margin:0px">Buy 2+ Save 50%</span><br>
{% endif %}
{% if product.tags contains 'B2S30' %}
<span class="badge color-accent-2" aria-hidden="true" style="float:right;margin:0px">Buy 2+ Save 30%</span><br>
{% endif %}
{% if product.tags contains 'B10S30' %}
<span class="badge color-accent-2" aria-hidden="true" style="float:right;margin:0px">Buy 10+ Save 30%</span><br>
{% endif %}

{%assign amazon_affiliate = product.metafields.my_fields.amazon_affiliate %} {%assign amazon_affiliate_fr = product.metafields.my_fields.fr_amazon_affiliate %} {%assign amazon_affiliate_de = product.metafields.my_fields.de_amazon_affiliate %}  <div id="AMZ_UK" style="display:none">
  {% if amazon_affiliate %}
  <label class="form__label" style="margin-bottom:0px"> Available at:</label>
  <label>
    <a href="{{ amazon_affiliate }}" class="mobile_external" target="_blank">
      <img src="https://cdn.shopify.com/s/files/1/0219/0638/t/21/assets/favicon_amazon.ico?v=1629853280" width="16px" height="16px" style="vertical-align:middle;margin-right:5px"><span class="form__label" style="display:inherit">Amazon</span></span></a>
<span class="form__label" style="display:inherit">
     {{ product.price | money_with_currency }}
  </label>
  {% endif %}
</div>

<div id="AMZ_DE" style="display:none"> 
  {% if amazon_affiliate_de %}
  <label class="form__label" style="margin-bottom:0px">Available at:</label>
  <label>
    <a href="{{ amazon_affiliate_de }}" class="mobile_external" target="_blank">
      <img src="https://cdn.shopify.com/s/files/1/0219/0638/t/21/assets/favicon_amazon.ico?v=1629853280" width="16px" height="16px" style="vertical-align:middle;margin-right:5px"><span class="form__label" style="display:inherit">Amazon</span></span></a>
<span class="form__label" style="display:inherit">
     {{ product.price | money_with_currency }}
  </label>
  {% endif %}
 </div>  
<div id="AMZ_FR" style="display:none"> 
  {% if amazon_affiliate_fr %}
  <label class="form__label" style="margin-bottom:0px">Available at:</label>
  <label>
    <a href="{{ amazon_affiliate_fr }}" class="mobile_external" target="_blank">
      <img src="https://cdn.shopify.com/s/files/1/0219/0638/t/21/assets/favicon_amazon.ico?v=1629853280" width="16px" height="16px" style="vertical-align:middle;margin-right:5px"><span class="form__label" style="display:inherit">Amazon</span></span></a>
<span class="form__label" style="display:inherit">
     {{ product.price | money_with_currency }}
  </label>
  {% endif %}
  </div>
     
<script type="text/javascript">
if(location.hostname == "lifxshop.co.uk"){ document.getElementById("AMZ_UK").style.display = "";
} else if (location.hostname == "lifxshop.de") { document.getElementById("AMZ_DE").style.display = "";
} else if (location.hostname == "lifxshop.fr") { document.getElementById("AMZ_FR").style.display = "";
}
</script>

{% if product.available%}
<span class="badge" aria-hidden="true" style="margin:0px">&#128994;&nbsp;&nbsp;In stock</span>
{% else %}
<span class="badge" aria-hidden="true" style="margin:0px">&#128308;
&nbsp;&nbsp;Out of stock</span>
{% endif %}
